import React from 'react';
import styled, { keyframes } from 'styled-components';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(-10px); }
  to { opacity: 1; transform: translateY(0); }
`;

const HeaderBar = styled.header`
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10;
  background: none;
  box-shadow: none;
  backdrop-filter: none;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 44px;
  animation: ${fadeIn} 0.7s cubic-bezier(.4,0,.2,1);
`;

const Nav = styled.nav`
  width: 100%;
  max-width: 1280px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 24px;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 28px;
  align-items: center;
`;

const NavLink = styled.a`
  color: #eaf6ff;
  font-size: 1rem;
  font-weight: 500;
  text-decoration: none;
  letter-spacing: 0.04em;
  position: relative;
  transition: color 0.2s;
  padding: 2px 0;
  &:after {
    content: '';
    display: block;
    height: 2px;
    width: 0;
    background: linear-gradient(90deg, #0096ff, #6a67ce);
    transition: width 0.3s;
    border-radius: 2px;
    margin-top: 2px;
  }
  &:hover {
    color: #6a67ce;
  }
  &:hover:after {
    width: 100%;
  }
`;

const GetAuraButton = styled.a`
  background: linear-gradient(90deg, #0096ff 0%, #6a67ce 100%);
  color: #fff;
  font-weight: 700;
  font-size: 0.98rem;
  padding: 7px 20px;
  border-radius: 24px;
  text-decoration: none;
  margin-left: 24px;
  box-shadow: none;
  transition: transform 0.16s, background 0.3s;
  letter-spacing: 0.08em;
  border: none;
  cursor: pointer;
  outline: none;
  &:hover {
    transform: translateY(-1px) scale(1.03);
    background: linear-gradient(90deg, #6a67ce 0%, #0096ff 100%);
  }
`;

const Header: React.FC = () => {
  return (
    <HeaderBar>
      <Nav>
        <NavLinks>
          <NavLink href="#about">About</NavLink>
          <NavLink href="#contacts">Contacts</NavLink>
        </NavLinks>
        <GetAuraButton href="#getaura">GET AURA</GetAuraButton>
      </Nav>
    </HeaderBar>
  );
};

export default Header;
