import React, { useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import AuraLogo3D from './AuraLogo3D';
import Header from './Header';
import Footer from './Footer';
import { locales, LocaleKey } from '../locales';

const GlobalStyle = createGlobalStyle`
  :root {
    --deep-blue: #0A2540;
    --teal: #00C9B1;
    --purple: #6A67CE;
    --gold: #FFD700;
  }

  body {
    font-family: 'Inter', sans-serif;
    margin: 0;
    background: linear-gradient(135deg, #070a13 0%, #101326 100%);
    min-height: 100vh;
    overflow: hidden;
  }
`;

const Centered = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
`;

// Центрированный языковой переключатель
const CenteredLangSwitcher = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 100;
  display: flex;
  align-items: center;
  font-size: 1.2rem;
  font-weight: 700;
  color: #eaf6ff;
  background: rgba(10, 37, 64, 0.85);
  border-radius: 18px;
  padding: 18px 36px;
  box-shadow: 0 4px 32px 0 rgba(0,150,255,0.10);
  @media (max-width: 600px) {
    font-size: 1rem;
    padding: 10px 18px;
  }
`;

const LangButton = styled.button<{active?: boolean}>`
  background: none;
  border: none;
  color: ${({active}) => active ? '#0096ff' : 'rgba(234,246,255,0.6)'};
  font-weight: 700;
  font-size: 1.2rem;
  cursor: pointer;
  padding: 0 8px;
  transition: color 0.2s;
  outline: none;
  &:hover {
    color: #6a67ce;
  }
`;

const LandingPage: React.FC = () => {
  const [language, setLanguage] = useState<LocaleKey>('en');
  const messages = locales[language];

  return (
    <>
      <GlobalStyle />
      <Header language={language} onLanguageChange={setLanguage} messages={messages} />
      <CenteredLangSwitcher>
        <LangButton
          active={language === 'ru'}
          onClick={() => setLanguage('ru')}
          aria-label="Русский"
        >RU</LangButton>
        <span style={{margin: '0 8px', color: '#6a67ce'}}>|</span>
        <LangButton
          active={language === 'en'}
          onClick={() => setLanguage('en')}
          aria-label="English"
        >EN</LangButton>
      </CenteredLangSwitcher>
      <Centered>
        <AuraLogo3D subtitle={messages.subtitle} subtitleButtonText={messages.subtitleButtonText} />
      </Centered>
      <Footer message={messages.footer} />
    </>
  );
};

export default LandingPage;
