import React from 'react';
import styled, { keyframes } from 'styled-components';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(-10px); }
  to { opacity: 1; transform: translateY(0); }
`;

const HeaderBar = styled.header`
  width: 100%;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10;
  background: none;
  box-shadow: none;
  backdrop-filter: none;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 56px;
  animation: ${fadeIn} 0.7s cubic-bezier(.4,0,.2,1);
`;

const Nav = styled.nav`
  width: 100%;
  max-width: 1280px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 24px;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 28px;
  align-items: center;
`;

const NavLink = styled.a`
  color: #eaf6ff;
  font-size: 1rem;
  font-weight: 500;
  text-decoration: none;
  letter-spacing: 0.04em;
  position: relative;
  transition: color 0.2s;
  padding: 10px 0;
  &:after {
    content: '';
    display: block;
    height: 2px;
    width: 0;
    background: linear-gradient(90deg, #0096ff, #6a67ce);
    transition: width 0.3s;
    border-radius: 2px;
    margin-top: 2px;
  }
  &:hover {
    color: #6a67ce;
  }
  &:hover:after {
    width: 100%;
  }
`;

const RightBlock = styled.div`
  display: flex;
  align-items: center;
  gap: 18px;
`;

const LangSwitcher = styled.div`
  display: flex;
  align-items: center;
  font-size: 0.98rem;
  font-weight: 600;
  color: #eaf6ff;
  user-select: none;
  gap: 6px;
`;

const LangButton = styled.button<{active?: boolean}>`
  background: none;
  border: none;
  color: ${({active}) => active ? '#0096ff' : 'rgba(234,246,255,0.6)'};
  font-weight: 700;
  font-size: 0.98rem;
  cursor: pointer;
  padding: 0 2px;
  transition: color 0.2s;
  outline: none;
  &:hover {
    color: #6a67ce;
  }
`;

interface HeaderProps {
  language?: 'ru' | 'en';
  onLanguageChange?: (lang: 'ru' | 'en') => void;
  messages: {
    about: string;
    contacts: string;
    getAura: string;
  };
}

const Header: React.FC<HeaderProps> = ({ language = 'en', onLanguageChange, messages }) => {
  return (
    <HeaderBar>
      <Nav>
        <NavLinks>
          <NavLink href="#about">{messages.about}</NavLink>
          <NavLink href="#contacts">{messages.contacts}</NavLink>
        </NavLinks>
        <RightBlock>
          <LangSwitcher>
            <LangButton
              active={language === 'ru'}
              onClick={() => onLanguageChange && onLanguageChange('ru')}
              aria-label="Русский"
            >RU</LangButton>
            <span>|</span>
            <LangButton
              active={language === 'en'}
              onClick={() => onLanguageChange && onLanguageChange('en')}
              aria-label="English"
            >EN</LangButton>
          </LangSwitcher>
        </RightBlock>
      </Nav>
    </HeaderBar>
  );
};

export default Header;
