import React from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import Logo from './Logo';

const GlobalStyle = createGlobalStyle`
  :root {
    --deep-blue: #0A2540;
    --teal: #00C9B1;
    --purple: #6A67CE;
    --gold: #FFD700;
  }

  body {
    font-family: 'Inter', sans-serif;
    margin: 0;
    background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  }
`;

const Hero = styled.section`
  text-align: center;
  padding: 80px 10%;
  background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="none"><path d="M0,0 L100,0 L100,100 Q50,80 0,100 Z" fill="%2300c9b122"/></svg>') no-repeat bottom/100% auto;
`;

const GradientTitle = styled.h1`
  font-size: 3.5rem;
  background: linear-gradient(90deg, var(--teal), var(--purple));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 20px;
`;

const CTAButton = styled.a`
  background: var(--deep-blue);
  color: white;
  padding: 15px 30px;
  border-radius: 30px;
  font-weight: 600;
  display: inline-block;
  margin-top: 20px;
  transition: all 0.3s;
  text-decoration: none;
  cursor: pointer;

  &:hover {
    transform: translateY(-5px);
    background: linear-gradient(90deg, var(--teal), var(--purple));
  }
`;

const Features = styled.section`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 30px;
  padding: 50px 5%;
`;

const FeatureCard = styled.div`
  background: white;
  border-radius: 15px;
  padding: 25px;
  width: 250px;
  box-shadow: 0 10px 30px rgba(10, 37, 64, 0.1);
  transition: transform 0.3s;

  &:hover {
    transform: translateY(-5px);
  }

  h3 {
    color: var(--deep-blue);
    margin-top: 0;
  }

  p {
    color: #666;
    line-height: 1.6;
  }
`;

const LandingPage: React.FC = () => {
  return (
    <>
      <GlobalStyle />
      <Hero>
        <GradientTitle>Ваш бизнес без рутины</GradientTitle>
        <p>Автоматизируем процессы с помощью AI: от коммуникаций до администрирования</p>
        <CTAButton href="#demo">Запросить демо</CTAButton>
      </Hero>
      <Features>
        <FeatureCard>
          <h3>🤖 Автоматизация сценариев</h3>
          <p>Строим процессы под ваши задачи, а не наоборот.</p>
        </FeatureCard>
        <FeatureCard>
          <h3>⚡ Интеграция AI-инструментов</h3>
          <p>Внедряем и настраиваем лучшие решения на рынке.</p>
        </FeatureCard>
        <FeatureCard>
          <h3>🛡️ Полное администрирование</h3>
          <p>Ваши системы работают, пока вы спите.</p>
        </FeatureCard>
      </Features>
    </>
  );
};

export default LandingPage;
