import React, { useEffect, useRef, useState } from 'react';
import styled, { keyframes } from 'styled-components';

const pulse = keyframes`
  0%, 100% { transform: scale(1); opacity: 0.8; }
  50% { transform: scale(1.1); opacity: 1; }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const AuraContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AuraEffect = styled.div<{bg: string}>`
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  background: ${({ bg }) => bg};
  animation: ${pulse} 6s infinite ease-in-out;
  filter: blur(20px);
  z-index: 1;
  transition: background 2s;
`;

const Ring = styled.div`
  position: absolute;
  width: 80%;
  height: 80%;
  border-radius: 50%;
  border: 2px solid rgba(255, 255, 255, 0.1);
  box-shadow: 0 0 40px rgba(0, 201, 177, 0.3);
  z-index: 2;
  animation: ${rotate} 20s linear infinite;
`;

const LogoMain = styled.div`
  position: relative;
  font-size: 120px;
  font-weight: 700;
  background: linear-gradient(135deg, #00c9b1 0%, #6a67ce 100%);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  text-shadow: 0 0 30px rgba(106, 103, 206, 0.4);
  z-index: 3;
  letter-spacing: 5px;
  animation: ${float} 3s infinite ease-in-out;
  user-select: none;
`;

const Particles = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const particleMove = keyframes`
  0% { transform: rotate(0deg) translateX(200px) rotate(0deg); opacity: 0; }
  10% { opacity: 1; }
  90% { opacity: 1; }
  100% { transform: rotate(360deg) translateX(200px) rotate(-360deg); opacity: 0; }
`;

const Particle = styled.div<{
  top: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
}>`
  position: absolute;
  top: ${({ top }) => top}%;
  left: ${({ left }) => left}%;
  width: ${({ size }) => size}px;
  height: ${({ size }) => size}px;
  background: ${({ color }) => color};
  border-radius: 50%;
  animation: ${particleMove} ${({ duration }) => duration}s linear ${({ delay }) => delay}s infinite;
`;

const AURA_COLORS = [
  'rgba(106, 103, 206, 0.3)', // фиолетовый
  'rgba(0, 150, 255, 0.3)',  // синий
  'rgba(150, 0, 255, 0.3)'   // фиолетово-розовый
];

const PARTICLE_COLORS = [
  'linear-gradient(135deg, #00c9b1, #6a67ce)',
  'linear-gradient(135deg, #6a67ce, #00c9b1)',
  '#00c9b1',
  '#6a67ce'
];

const PARTICLE_COUNT = 50;

const AuraLogo3D: React.FC = () => {
  const [auraColorIdx, setAuraColorIdx] = useState(0);
  const [auraBg, setAuraBg] = useState(
    `radial-gradient(circle at center, ${AURA_COLORS[0]} 0%, rgba(106, 103, 206, 0.15) 40%, transparent 70%)`
  );

  useEffect(() => {
    const interval = setInterval(() => {
      setAuraColorIdx((idx) => (idx + 1) % AURA_COLORS.length);
    }, 8000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    setAuraBg(
      `radial-gradient(circle at center, ${AURA_COLORS[auraColorIdx]} 0%, rgba(106, 103, 206, 0.15) 40%, transparent 70%)`
    );
  }, [auraColorIdx]);

  // Генерируем частицы один раз
  const particles = useRef(
    Array.from({ length: PARTICLE_COUNT }).map(() => {
      const size = Math.random() * 4 + 2;
      const duration = Math.random() * 10 + 10;
      const delay = Math.random() * 5;
      const color = PARTICLE_COLORS[Math.floor(Math.random() * PARTICLE_COLORS.length)];
      const angle = Math.random() * 360;
      // Для равномерного распределения по окружности
      const top = 50 + 40 * Math.sin((angle * Math.PI) / 180);
      const left = 50 + 40 * Math.cos((angle * Math.PI) / 180);
      return { size, duration, delay, color, top, left };
    })
  );

  return (
    <AuraContainer>
      <AuraEffect bg={auraBg} />
      <Ring />
      <Particles>
        {particles.current.map((p, i) => (
          <Particle
            key={i}
            size={p.size}
            duration={p.duration}
            delay={p.delay}
            color={p.color}
            top={p.top}
            left={p.left}
          />
        ))}
      </Particles>
      <LogoMain>AURA</LogoMain>
    </AuraContainer>
  );
};

export default AuraLogo3D;
