import React, { useEffect, useRef, useState } from 'react';
import styled, { keyframes } from 'styled-components';

const pulse = keyframes`
  0%, 100% { transform: scale(1); opacity: 0.8; }
  50% { transform: scale(1.1); opacity: 1; }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
`;

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const rotateReverse = keyframes`
  from { transform: rotate(360deg); }
  to { transform: rotate(0deg); }
`;

const AuraContainer = styled.div`
  position: relative;
  width: 600px;
  height: 600px;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AuraRing = styled.div<{
  color1: string;
  color2: string;
  blur: number;
  size: string;
  duration: string;
  reverse?: boolean;
}>`
  position: absolute;
  width: ${({ size }) => size};
  height: ${({ size }) => size};
  border-radius: 50%;
  pointer-events: none;
  z-index: 1;
  background: conic-gradient(
    ${({ color1, color2 }) => `${color1} 0deg, ${color2} 180deg, ${color1} 360deg`}
  );
  opacity: 0.45;
  filter: blur(${({ blur }) => blur}px) drop-shadow(0 0 40px ${({ color1 }) => color1});
  animation: ${({ reverse }) => reverse ? rotateReverse : rotate} ${({ duration }) => duration} linear infinite;
`;

const Ring = styled.div`
  position: absolute;
  width: 80%;
  height: 80%;
  border-radius: 50%;
  border: 2px solid rgba(255, 255, 255, 0.1);
  box-shadow: 0 0 40px rgba(0, 201, 177, 0.3);
  z-index: 2;
  animation: ${rotate} 20s linear infinite;
`;

const LogoMain = styled.div`
  position: relative;
  font-size: 120px;
  font-weight: 700;
  background: linear-gradient(135deg, #00c9b1 0%, #6a67ce 100%);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  text-shadow: 0 0 30px rgba(106, 103, 206, 0.4);
  z-index: 3;
  letter-spacing: 5px;
  animation: ${float} 3s infinite ease-in-out;
  user-select: none;
`;

const Particles = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const particleMove = keyframes`
  0% { transform: rotate(0deg) translateX(200px) rotate(0deg); opacity: 0; }
  10% { opacity: 1; }
  90% { opacity: 1; }
  100% { transform: rotate(360deg) translateX(200px) rotate(-360deg); opacity: 0; }
`;

const Particle = styled.div<{
  top: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
}>`
  position: absolute;
  top: ${({ top }) => top}%;
  left: ${({ left }) => left}%;
  width: ${({ size }) => size}px;
  height: ${({ size }) => size}px;
  background: ${({ color }) => color};
  border-radius: 50%;
  animation: ${particleMove} ${({ duration }) => duration}s linear ${({ delay }) => delay}s infinite;
`;

const AURA_COLORS = [
  'rgba(0, 201, 177, 0.3)',
  'rgba(106, 103, 206, 0.3)',
  'rgba(0, 150, 255, 0.3)',
  'rgba(150, 0, 255, 0.3)'
];

const PARTICLE_COLORS = [
  'linear-gradient(135deg, #00c9b1, #6a67ce)',
  'linear-gradient(135deg, #6a67ce, #00c9b1)',
  '#00c9b1',
  '#6a67ce'
];

const PARTICLE_COUNT = 50;

const AuraLogo3D: React.FC = () => {
  // Генерируем частицы один раз
  const particles = useRef(
    Array.from({ length: PARTICLE_COUNT }).map(() => {
      const size = Math.random() * 4 + 2;
      const duration = Math.random() * 10 + 10;
      const delay = Math.random() * 5;
      const color = PARTICLE_COLORS[Math.floor(Math.random() * PARTICLE_COLORS.length)];
      const angle = Math.random() * 360;
      // Для равномерного распределения по окружности
      const top = 50 + 40 * Math.sin((angle * Math.PI) / 180);
      const left = 50 + 40 * Math.cos((angle * Math.PI) / 180);
      return { size, duration, delay, color, top, left };
    })
  );

  return (
    <AuraContainer>
      {/* Внешняя аура — бирюзово-фиолетовая, медленнее */}
      <AuraRing color1="#00c9b1" color2="#6a67ce" blur={40} size="100%" duration="18s" />
      {/* Внутренняя аура — фиолетово-голубая, быстрее и в обратную сторону */}
      <AuraRing color1="#6a67ce" color2="#0096ff" blur={30} size="75%" duration="10s" reverse />
      <Ring />
      <Particles>
        {particles.current.map((p, i) => (
          <Particle
            key={i}
            size={p.size}
            duration={p.duration}
            delay={p.delay}
            color={p.color}
            top={p.top}
            left={p.left}
          />
        ))}
      </Particles>
      <LogoMain>AURA</LogoMain>
    </AuraContainer>
  );
};

export default AuraLogo3D;
